import React , {Component} from 'react'
import Header from './components/Header'
import Footer from './components/Footer'
import Product from './components/Product'
import 'bootstrap/dist/css/bootstrap.css';
import TaskList from './todo/TaskList'

class App extends Component{


    state = {
      count:5,
      tasks: [ {id:"1" , task:"coding"} ,
               {id:"2" , task:"reading"}],
      id : 3
    }

    constructor(props){
      super(props)
      this.add = this.add.bind(this)
      this.sub = this.sub.bind(this)
    }

    add = () => {
    this.setState({
      count:this.state.count += 1
      })
    }
    sub = () => {
      this.setState({
        count:this.state.count -= 1
        })
      }

    add = (task) => {
      this.setState({
        tasks: [...this.state.tasks  , {id: this.state.id,task}] ,
        id : this.state.id+1
      })
    }


    render () {
      return (
        <div>
          <Header name="Supanut" id="049"/>
          <hr />
            <h3>Counter : {this.state.count}</h3>
            <button className="btn btn-success " onClick={this.add}>Up</button>
            <button className="btn btn-danger " onClick={this.sub}>down</button>
          <hr />
          <Product productname="Macbook" productprice="53000" />
          <Product productname="iPhoneX" productprice="41000" />
          <hr />

          <h1>ToDo</h1>
          <TaskList tasks={this.state.tasks}/>
          <inputTask addTask={this.addTask} id={this.state.id} />
          <hr />
          <Footer />
        </div>
      )

    }
}

export default App
