import React , {Component} from 'react'
import Header from './components/Header'
import Footer from './components/Footer'
import 'bootstrap/dist/css/bootstrap.css';
import TaskList from './todo/TaskList';
import InputTask from './todo/InputTask';
import Github from "./Github/index";
import Middle from './components/mech/Middle';

class App extends Component{


    state = {
      count:5,
      tasks: [ {id:"1" , task:"coding" ,location:'lab' ,date:new Date()} ,
               {id:"2" , task:"reading",location:"library" ,date:new Date()}],
      pokemons:"",
      id : 3
    }

    constructor(props){
      super(props)
      this.add = this.add.bind(this)
      this.sub = this.sub.bind(this)
    }

    add = () => {
    this.setState({
      count:this.state.count + 1
      })
    }
    sub = () => {
      this.setState({
        count:this.state.count - 1
        })
      }

    addTask = (task,location,date) => {
      this.setState({
        tasks: [...this.state.tasks  , {id: this.state.id,task,location,date:new Date()}] ,

        id : this.state.id+1
      })
    }

    componentDidMount(){
      this.setState({pokemons:[
        { id:1 , pokemonName: "Charmander" , power: "200" ,thumbnail: "" },
        { id:2 , pokemonName: "charmeleon" , power: "700" ,thumbnail: "" },
        { id:3 , pokemonName: "Charizard" , power: "3000" ,thumbnail: "" },
        { id:4 , pokemonName: "Bulbasaur" , power: "200" ,thumbnail: "" },
        { id:5 , pokemonName: "Lvysaur" , power: "750" ,thumbnail: "" },
        { id:6 , pokemonName: "Venusaur" , power: "2500" ,thumbnail: "" },
        { id:7 , pokemonName: "Squirtle" , power: "400" ,thumbnail: "" },
        { id:8 , pokemonName: "Wartortle" , power: "1500" ,thumbnail: "" },
        { id:9 , pokemonName: "Blastoise" , power: "2900" ,thumbnail: "" },
    ]})

    }

    render () {
      // console.log(this.state.pokemons)
      return (
        <div>
          <Header/>
          <hr />
            <h3>Counter : {this.state.count}</h3>
            <button className="btn btn-success " onClick={this.add}>Up</button>
            <button className="btn btn-danger " onClick={this.sub}>down</button>
          <h1>ToDo</h1>
          <TaskList tasks={this.state.tasks} date={this.state.date} />
          <InputTask addTask={this.addTask} id={this.state.id} />
          <hr />
          <Github/>
          <hr />
          <Middle pokemons={this.state.pokemons}/>
          <hr />
          <Footer name="Supanut Laddayam" id="5935512049"/>
        </div>
      )

    }
}

export default App
