import React , {Component} from 'react'
import Header from './components/Header'
import Footer from './components/Footer'
import Product from './components/Product'
import './custom.scss';
class App extends Component{


  // const Show = () =>  {
  //   return (
  //     <h1>hello Supanut</h1>
  //   )
  // }
    state = {
      count:5
    }

    constructor(props){
      super(props)
      this.add = this.add.bind(this)
      this.sub = this.sub.bind(this)
    }

    add = () => {
    this.setState({
      count:this.state.count += 1
      })
    }
    sub = () => {
      this.setState({
        count:this.state.count -= 1
        })
      }

    render () {
      return (
        <div>
          <Header name="Supanut" id="049"/>
          <hr />
            <h3>Counter : {this.state.count}</h3>
            <button className="btn btn-danger " onClick={this.add}>Up</button>
            <button className="btn btn-danger " onClick={this.sub}>down</button>
          <hr />
          <Product productname="Macbook" productprice="53000" />
          <Product productname="iPhoneX" productprice="41000" />
          <Footer />
        </div>
      )

    }
}

export default App
